Raphael.fn.polygon = function (pointString) {
    var poly = ['M'];
    var point = pointString.split(' ');

    for (var i = 0; i < point.length; i++) {
        var c = point[i].split(',');
        for (var j = 0; j < c.length; j++) {
            var d = parseFloat(c[j]);
            if (!isNaN(d))
                poly.push(d);
        }
        if (i == 0)
            poly.push('L');
    }
    poly.push('Z');

    return this.path(poly);
};
